(15, 'string', 'content_en', NULL, NULL, NULL, '<div style=\"margin-bottom: 15px;\">\r\n<div style=\"font-weight: bold; clear: both;\">Repozytorium Lokalne\r\n<div style=\"font-weight: normal; display: inline;\">- dzia powicony dziaaniom podejmowanym w folderach lokalnych</div>\r\n</div>\r\n</div>\r\n<div style=\"margin-bottom: 15px;\">\r\n<div style=\"font-weight: bold; clear: both;\">Repozytorium Zdalne\r\n<div style=\"font-weight: normal; display: inline;\">- dzia powicony dziaaniom podejmowanym na serwerach</div>\r\n</div>\r\n</div>\r\n<div style=\"margin-bottom: 15px;\">\r\n<div style=\"font-weight: bold; clear: both;\">Obsuga Gazi\r\n<div style=\"font-weight: normal; display: inline;\">- dzia powicony dziaaniom zwizanym z zarzdzaniem gaziami w projektach</div>\r\n</div>\r\n</div>\r\n<div style=\"margin-bottom: 15px;\">\r\n<div style=\"font-weight: bold; clear: both;\">Usuwanie\r\n<div style=\"font-weight: normal; display: inline;\">- dzia powicony dziaaniom zwizanym z usuwaniem projekt&oacute;w</div>\r\n</div>\r\n</div>\r\n<div style=\"margin-bottom: 15px;\">\r\n<div style=\"font-weight: bold; clear: both;\">Praca Zespoowa\r\n<div style=\"font-weight: normal; display: inline;\">- dzia powicony wsp&oacute;pracy pomidzy uytkownikami</div>\r\n</div>\r\n</div>\r\n<div style=\"margin-bottom: 15px;\">\r\n<div style=\"font-weight: bold; clear: both;\">Pull Request\r\n<div style=\"font-weight: normal; display: inline;\">- dzia powicony dziaaniom zwizanym z udostpnianiem ukoczonych modu&oacute;w projektu</div>\r\n</div>\r\n</div>\r\n<div style=\"margin-bottom: 15px;\">\r\n<div style=\"font-weight: bold; clear: both;\">Inne\r\n<div style=\"font-weight: normal; display: inline;\">- dzia powicony rozwizywaniu problem&oacute;w</div>\r\n</div>\r\n</div>', NULL, '2019-11-05 05:55:33'),
