(17, 'default', '::-moz-selection {\r\n  background: #3498db;\r\n  color: #fff;\r\n  text-shadow: none;\r\n}\r\n::selection {\r\n  background: #3498db;\r\n  color: #fff;\r\n  text-shadow: none;\r\n}\r\n\r\n\r\na {\r\n	color: #3498db;\r\n}\r\n\r\na:hover {\r\n	color:#2980b9;\r\n}\r\n\r\nfooter .nav-list li a:hover,footer ul.regular li a:hover  {\r\n	color:#2980b9;\r\n}\r\n\r\n.btn-link:hover {\r\n  color: #2980b9;\r\n}\r\n\r\n.btn-theme {\r\n  background: #3498db;\r\n}\r\n.btn-theme:hover,\r\n.btn-theme.active,\r\n.btn-theme.disabled,\r\n.btn-theme[disabled] {\r\n  background: #2980b9;\r\n  *background: #2980b9;\r\n}\r\n\r\n.rheading span {\r\n  background: #3498db;\r\n}\r\n/* sequence slider */\r\n\r\n.info  h2{\r\n	background: #3498db;\r\n}\r\n\r\n\r\n/* iview slider */\r\n.iview-caption.caption-theme {\r\n	background: #3498db;\r\n}\r\n\r\n/* flexslider */\r\n.flex-caption {\r\n	border-bottom: 2px solid #3498db;\r\n}\r\n\r\n/* fontawesome icons */\r\n.icon-square:hover,\r\n.icon-rounded:hover,\r\n.icon-circled:hover {\r\n    background-color: #3498db;\r\n}\r\n[class^=\"icon-\"].active,\r\n[class*=\" icon-\"].active {\r\n    background-color: #3498db;\r\n}\r\n\r\n/* header */\r\n.navbar .nav li.active a {\r\n	background:#3498db;\r\n}\r\n.navbar .nav li.active a:hover {\r\n	background:#2980b9;\r\n}\r\n\r\n.color {\r\n	background: #3498db;\r\n	color:#fff;\r\n}\r\n\r\ntextarea:focus,\r\ninput[type=\"text\"]:focus,\r\ninput[type=\"password\"]:focus,\r\ninput[type=\"datetime\"]:focus,\r\ninput[type=\"datetime-local\"]:focus,\r\ninput[type=\"date\"]:focus,\r\ninput[type=\"month\"]:focus,\r\ninput[type=\"time\"]:focus,\r\ninput[type=\"week\"]:focus,\r\ninput[type=\"number\"]:focus,\r\ninput[type=\"email\"]:focus,\r\ninput[type=\"url\"]:focus,\r\ninput[type=\"search\"]:focus,\r\ninput[type=\"tel\"]:focus,\r\ninput[type=\"color\"]:focus,\r\n.uneditable-input:focus {\r\n  border-color: #3498db;\r\n  outline: 0;\r\n  outline: thin dotted \\9;\r\n  /* IE6-9 */\r\n\r\n}\r\n\r\n/* sequence slider */\r\n.sequence-nav .next:hover,.sequence-nav .prev:hover {\r\n	background: #3498db;\r\n}\r\n\r\n\r\n/* jcarousel */\r\n.jcarousel-skin-tango .jcarousel-prev-disabled-horizontal,\r\n.jcarousel-skin-tango .jcarousel-prev-disabled-horizontal:hover,\r\n.jcarousel-skin-tango .jcarousel-prev-disabled-horizontal:focus,\r\n.jcarousel-skin-tango .jcarousel-prev-disabled-horizontal:active {\r\n	background-color:#3498db;\r\n}\r\n\r\n/* breadcrumb */\r\n.breadcrumb {\r\n\r\n}\r\n\r\n/* call action */\r\n.call-action {\r\n	border-left:5px solid #3498db;\r\n}\r\n\r\n/* box */\r\n\r\n.features:hover .box {\r\n	border-bottom: 2px solid #3498db;\r\n}\r\n\r\n/* accordion */\r\n.accordion-heading a:hover {\r\n	color: #3498db;\r\n}\r\n.accordion-heading .active{\r\n	color: #3498db;\r\n}\r\n\r\n/* tab */\r\n.tabs-top .nav-tabs > li.active > a:hover,\r\n.tabs-top .nav-tabs > li.active > a { border-top-color: #3498db; }\r\n\r\n/* pricing box */\r\n.priceBox.special .emphasis {\r\n  background: #3498db;\r\n}\r\n\r\n/* tagline */\r\n.tagline {\r\n	border-bottom:5px solid #3498db;\r\n}\r\n\r\n\r\n\r\n/* button */\r\n.btn-link {\r\n	color: #3498db;\r\n}\r\n\r\n.nav .dropdown-toggle .caret {\r\n  margin-top: 6px;\r\n  border-top-color: #3498db;\r\n  border-bottom-color: #3498db;\r\n}\r\n\r\n\r\n\r\n/* tabs */\r\n.nav-pills > .active > a,\r\n.nav-pills > .active > a:hover {\r\n  color: #ffffff;\r\n  background-color: #3498db;\r\n}\r\n\r\n/* accordion */\r\n.accordion-heading a.accordion-toggle:hover {\r\n	color: #3498db;\r\n}\r\n\r\n/* label */\r\n.label-orange,\r\n.badge-orange {\r\n  background-color: #3498db;\r\n}\r\n\r\n\r\n/* nav */\r\n.nav-list > .active > a,\r\n.nav-list > .active > a:hover {\r\n  background: #3498db;\r\n}\r\n\r\n\r\n.nav-pills > .active > a,\r\n.nav-pills > .active > a:hover {\r\n  background: #3498db;\r\n}\r\n\r\n.nav .dropdown-toggle .caret {\r\n  border-top-color: #3498db;\r\n  border-bottom-color: #3498db;\r\n}\r\n\r\n/* custom progress bar according template skin */\r\n.progress-orange .bar,\r\n.progress .bar-orange {\r\n  background: #3498db;\r\n}\r\n\r\n.progress-orange.progress-striped .bar,\r\n.progress-striped .bar-orange {\r\n  background: #3498db;\r\n}\r\n\r\n/* pagination */\r\n\r\n.pagination ul li a:hover {\r\n  background: #3498db;\r\n  border-color:#3498db;\r\n}\r\n\r\n\r\n/* widget */\r\naside .widget ul.cat li a:hover {\r\n	color: #3498db;\r\n}\r\n\r\naside .widget ul.tags li a {\r\n  background: #3498db;\r\n}\r\n\r\naside .widget ul.recent-posts li a:hover {\r\n	color: #3498db;\r\n}\r\n\r\n/* flickr footer badge */\r\n\r\nfooter .widget .flickr-badge img:hover {\r\n	border:2px solid #3498db;\r\n}\r\n\r\n/* blog */\r\n\r\n.post-meta {\r\n	border-left:2px solid #3498db;\r\n}\r\n\r\n.entry-body a h5.title:hover {\r\n	color: #3498db;\r\n}\r\n\r\narticle.blog-post .post-heading h3 a:hover {\r\n	color:#3498db;\r\n}\r\n\r\narticle.last-post {\r\n	background: #3498db;\r\n}\r\n\r\nul.time-post li.year{\r\n	border-bottom:1px solid #3498db;\r\n}\r\n\r\nsection#subintro {\r\n	background: #3498db;\r\n}\r\n\r\n/* footer */\r\n\r\n\r\n\r\n /* 404 */\r\n \r\n\r\n.error-body{\r\n	background:#3498db;\r\n	padding:20px;\r\n	text-align:center;\r\n}\r\n\r\n\r\n/* misc */\r\na.thumbnail:hover {\r\n  border-color: #3498db;\r\n}\r\n\r\nul.filter li.active a:hover,.flex-control-nav li a:hover, .flex-control-nav li a.active{\r\n	background: #3498db;\r\n}\r\n\r\n.thumbnail h4 a:hover, .thumbnail .meta  span a:hover, ul.recent li h6 a:hover, ul.post-meta li a:hover, .media-body span, .media-body .media-heading a:hover, form label span, article.last-post h5 a:hover{\r\n	color: #3498db;\r\n	text-decoration:none;\r\n}', '', '', '', 1572491483, 1572491483);
