(25, 'Lumia - Page - Description', '{strip}\r\n	{process_pagedata}\r\n{/strip}<!doctype html>\r\n<html lang=\"{cms_get_language}\">\r\n\r\n<head>\r\n	<title>{title} - {sitename}</title>\r\n	{metadata}\r\n	{*{cms_stylesheet}*}\r\n\r\n\r\n\r\n    {include file=\'cms_template:Lumia - Core - css\'}\r\n\r\n</head>\r\n\r\n<body>\r\n\r\n\r\n\r\n<div id=\"wrapper\">\r\n    <header>\r\n        {*{Navigator template=\"cssmenu_ulshadow\"}\r\n        {include file=\'cms_template:Lumia - header\'}*}\r\n        <div class=\"navbar navbar-static-top\">\r\n            <div class=\"navbar-inner\">\r\n                <div class=\"container\">\r\n                    <div class=\"logo\">\r\n                    </div>\r\n                    \r\n                    <div class=\"navigation\">\r\n                        <nav>\r\n                    		{*{include file=\'cms_template:Lumia - header\'}*}\r\n                    		{Navigator template=\"Lumia - Core - Nav\"}\r\n        	            </nav>\r\n                    </div>\r\n\r\n        	    </div>\r\n        	</div>\r\n        </div>\r\n    </header>\r\n\r\n<section id=\"intro\">\r\n\r\n      <div class=\"container\">\r\n        <div class=\"row\">\r\n          <div class=\"span12\">\r\n            <!-- Place somewhere in the <body> of your page -->\r\n            <div id=\"mainslider\" class=\"flexslider\">\r\n\r\n                {*{include file=\'cms_template:Lumia - Core - Flexslider\'}*}\r\n              {Gallery dir={page_attr key=\'extra1\'} template=\"CycleForHome\"}\r\n\r\n          </div>\r\n        </div>\r\n      </div>\r\n\r\n    <section id=\"maincontent\">\r\n      <div class=\"container\">\r\n\r\n\r\n    {content}\r\n        {*{include file=\'cms_template:Lumia - Core - Recent\'}*}\r\n\r\n\r\n      </div>\r\n    </section>\r\n\r\n\r\n\r\n    </section>\r\n\r\n\r\n    <footer class=\"footer\">\r\n        {include file=\'cms_template:Lumia - Core - Footer\'}\r\n    </footer>\r\n\r\n</div>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<a h