(37, 'Lumia - Core - Javascript', '<script src=\"http://localhost/cmstri/uploads/js/jquery.js\"></script>\r\n  <script src=\"http://localhost/cmstri/uploads/js/raphael-min.js\"></script>\r\n  <script src=\"http://localhost/cmstri/uploads/js/jquery.easing.1.3.js\"></script>\r\n  <script src=\"http://localhost/cmstri/uploads/js/bootstrap.js\"></script>\r\n  <script src=\"http://localhost/cmstri/uploads/js/google-code-prettify/prettify.js\"></script>\r\n  <script src=\"http://localhost/cmstri/uploads/js/jquery.elastislide.js\"></script>\r\n  <script src=\"http://localhost/cmstri/uploads/js/jquery.prettyPhoto.js\"></script>\r\n  <script src=\"http://localhost/cmstri/uploads/js/jquery.flexslider.js\"></script>\r\n  <script src=\"http://localhost/cmstri/uploads/js/jquery-hover-effect.js\"></script>\r\n  <script src=\"http://localhost/cmstri/uploads/js/animate.js\"></script>\r\n\r\n  <!-- Template Custom JavaScript File -->\r\n  <script src=\"http://localhost/cmstri/uploads/js/custom.js\"></script>', '', 2, NULL, NULL, 1, 1, 1572644683, 1572644683),
(36, 'Lumia - Core - Breadcrumb', '{*\r\n<div class=\"container\">\r\n        <div class=\"row\">\r\n          <div class=\"span8\">\r\n            <ul class=\"breadcrumb\">\r\n              <li><a href=\"#\"><i class=\"icon-home\"></i></a><i class=\"icon-angle-right\"></i></li>\r\n              <li><a href=\"#\">Blog</a><i class=\"icon-angle-right\"></i></li>\r\n              <li class=\"active\">Blog with left sidebar</li>\r\n            </ul>\r\n          </div>\r\n          <div class=\"span4\">\r\n            <div class=\"search\">\r\n              <form class=\"input-append\">\r\n                <input class=\"search-form\" id=\"appendedPrependedInput\" type=\"text\" placeholder=\"Search here..\" />\r\n                <button class=\"btn btn-dark\" type=\"submit\">Search</button>\r\n              </form>\r\n            </div>\r\n          </div>\r\n        </div>\r\n      </div>\r\n  *}    \r\n{* default breadcrumbs template *}\r\n{$starttext=\'\'}\r\n<div class=\"container\">\r\n        <div class=\"row\">\r\n          <div class=\"span8\">\r\n            <ul class=\"breadcrumb\">\r\n{strip}\r\n{*<div class=\"breadcrumb\">*}\r\n  {*{if isset($starttext)}{$starttext}:{*&nbsp;{/if}*}\r\n  {foreach $nodelist as $node}\r\n    {$liclass=\'\'}\r\n    {if $node->current}\r\n      {$liclass=$liclass|cat:\'active\'}\r\n    {/if}\r\n\r\n    <li class=\"{$liclass}\">\r\n        \r\n      {if $node@last}\r\n        <i class=\"icon-angle-right\"></i>\r\n        {$node->menutext}\r\n      {elseif $node->type == \'sectionheader\'}\r\n        {$node->menutext}\r\n      {else}\r\n        {if $node@first}\r\n            <a href=\"{$node->url}\" title=\"{$node->menutext}\"><i class=\"icon-home\"></i></a>\r\n        {else}\r\n            <i class=\"icon-angle-right\"></i>\r\n            <a href=\"{$node->url}\" title=\"{$node->menutext}\">{$node->menutext}</a>\r\n        {/if}\r\n      {/if}\r\n      \r\n    </li>\r\n\r\n    {if !$node@last}{/if}\r\n  {/foreach}\r\n{*</div>*}\r\