(3, -1, 'Tutorial', '<p>tre artykuu</p>', '2019-02-03 06:57:31', '<p>podsumowanie</p>', NULL, NULL, 'published', NULL, '2019-11-03 06:58:14', '2019-11-03 21:33:12', 1, '', '', 1),
(4, 2, 'Dodawanie do kolejki', '<section></section>\r\n<section>\r\n<div class=\"post\">\r\n<div id=\"opis\" class=\"opis\">\r\n<ol>\r\n<li class=\"post-podpunkty\">sprawdzamy biecy status\r\n<div class=\"post-komendy\">git status</div>\r\n</li>\r\n<li class=\"post-podpunkty\">tworyzmy pliki\r\n<div class=\"post-komendy\">echo &gt;&gt; nowy.txt</div>\r\n<div class=\"post-komendy\">echo &gt;&gt; do_ukrycia.txt</div>\r\n</li>\r\n<li class=\"post-podpunkty\">dodajemy pliki do kolejki\r\n<div class=\"post-komendy\">git add .</div>\r\n</li>\r\n<li class=\"post-podpunkty\">sprawdzamy biecy status\r\n<div class=\"post-komendy\">git status</div>\r\n</li>\r\n<li class=\"post-podpunkty\">wprowadzamy zmiany do pliku i sprawdzamy biecy status\r\n<div class=\"post-komendy\">git status</div>\r\n<div class=\"post-komentarz\">mamy dwie wersje pliku i musimy zdecydowa co zrobi: usun kt&oacute;r kopi, przywr&oacute;ci oczekujc na zatwierdzenie lub ponownie doda plik do kolejki oczekiwania<br />na czerwono - pliki nie ledzone<br />na zielono - pliki ledzone</div>\r\n</li>\r\n<li class=\"post-podpunkty\">tworzymy commit\r\n<div class=\"post-komendy\">git commit -m \"Pierwsza rewizja\"</div>\r\n</li>\r\n</ol>\r\n</div>\r\n</div>\r\n</section>', '2019-11-03 08:20:52', '<div style=\"overflow: auto; margin-top: 10px;\"><img style=\"width: 200px; float: left;\" src=\"uploads/images/Gallery/Repozytorium Lokalne/Dodawanie do kolejki/Lokalne_03_kolory_plikow.jpg\" />\r\n<p style=\"min-height: 100px; display: flex; align-items: center; padding-left: 30px;\">Dodawanie do kolejki gita</p>\r\n</div>', NULL, NULL, 'published', NULL, '2019-11-03 08:21:22', '2019-11-04 10:11:35', 1, '', '', 1),
(2, 2, 'Tworzenie repozytorium', '<section></section>\r\n<section>\r\n<div class=\"post\">\r\n<div id=\"opis\" class=\"opis\">\r\n<ol>\r\n<li class=\"post-podpunkty\">Tworzymy nowy folder\r\n<div class=\"post-komendy\">mkdir nowy</div>\r\n</li>\r\n<li class=\"post-podpunkty\">wchodzimy do tego folderu\r\n<div class=\"post-komendy\">cd nowy</div>\r\n</li>\r\n<li class=\"post-podpunkty\">inicjalizujemy repozytorium\r\n<div class=\"post-komendy\">git init</div>\r\n</li>\r\n<li class=\"post-podpunkty\">wchodzimy do folderu poprzez bash git-a\r\n<div class=\"post-komendy\">&nbsp;</div>\r\n</li>\r\n</ol>\r\n</div>\r\n</div>\r\n</section>', '2019-10-03 06:47:23', '<div style=\"overflow: auto; margin-top: 10px;\"><img style=\"width: 200px; float: left;\" src=\"uploads/images/Gallery/Repozytorium Lokalne/Dodawanie do kolejki/Lokalne_03_kolory_plikow.jpg\" />\r\n<p style=\"min-height: 100px; display: flex; align-items: center; padding-left: 30px;\">W tym wpisie przedstawi czym jest repozytorium w Git, jak takie repo zainicjalizowa, sklonowa oraz jak si w nim porusza. Przedstawi kilka niezbdnych komend, bez kt&oacute;rych w tym cyklu ani rusz. Zapraszam</p>\r\n</div>', NULL, NULL, 'published', NULL, '2019-11-03 06:50:43', '2019-11-04 10:11:48', 1, '', '', 1),
(5, 3, 'Pobieranie repozytorium z githuba', '<section></section>\r\n<section>\r\n<div class=\"post\">\r\n<div id=\"opis\" class=\"opis\">\r\n<ol>\r\n<li class=\"post-podpunkty\">pobieramy adres ze strony githuba</li>\r\n<li class=\"post-podpunkty\">tworzymy nowy folder i wchodzimy do niego</li>\r\n<li class=\"post-podpunkty\">pobieramy wersj z serwera do nowego katalogu\r\n<div class=\"post-komendy\">git clone https://github.com/_nazwa_uytkownika_/learn-git.git</div>\r\n</li>\r\n</ol>\r\n</div>\r\n</div>\r\n</section>', '2019-11-04 00:32:54', '<div style=\"overflow: auto; margin-top: 10px;\"><img style=\"width: 200px; float: left;\" src=\"uploads/images/Gallery/Repozytorium Zdalne/Pobieranie repozytorium z githuba/Zdalne_12_pobranie_adresu_ze_strony_githuba.png\" />\r\n<p style=\"min-height: 100px; display: flex; align-items: center; padding-left: 30px;\">W tym wpisie przedstawi jak pobra repozytorium ze strony Github-a</p>\r\n</div>', NULL, NULL, 'published', NULL, '2019-11-04 00:33:30', '2019-11-04 10:11:15', 1, '', '', 1);
